import pandas as pd

# Load the data
data = pd.read_csv('infiagent/csv/3.csv')

# Define the function to grade the score 
def grade(score):
    if score >= 7:
        return 'A'
    elif score >= 6:
        return 'B'
    elif score >= 5:
        return 'C'
    elif score >= 4:
        return 'D'
    else:
        return 'F'

# Apply the function to the "Happiness Score" and add a new column "Grade" to the data.
data['Grade'] = data['Happiness Score'].apply(grade)

#Print the modified data
print(data)

