import pandas as pd

# Load the data from the csv file
df = pd.read_csv('data.csv')

# Define a function to categorize BMI
def categorize_bmi(bmi):
    if bmi < 18.5:
        return 'Underweight'
    elif 18.5 <= bmi <= 24.9:
        return 'Normal'
    elif 25 <= bmi <= 29.9:
        return 'Overweight'
    else:
        return 'Obese'

# Apply the function to the 'bmi' column to create the 'bmi_category' column
df['bmi_category'] = df['bmi'].apply(categorize_bmi)

print(df)

