import pandas as pd

# Load the data
data = pd.read_csv('infiagent/csv/50.csv')

# Function to categorize description
def categorize_weather(description):
    if 'rain' in description.lower():
        return 'Rain'
    elif 'cloud' in description.lower():
        return 'Cloud'
    elif 'sun' in description.lower():
        return 'Sun'
    else:
        return 'Other'

# Update 'description' column
data['description'] = data['description'].apply(categorize_weather)

print(data)
